import React, { useState,useRef } from "react";
import axios from "axios";
import ReactAudioPlayer from 'react-audio-player';
import { CameraFeed } from './components/camera-feed';

function Hang(){
    const [sound,setSound] = useState();
    const [infer,setInfer] = useState(false);
    const [player,setPlayer] = useState();
    const imgHandler = async file =>{
        const formData = new FormData();
        formData.append('img',file);
        axios.post("http://192.168.0.82:8000/app/imginf/",formData)
        // axios.post("http://localhost:8000/app/imginf/",formData)
        .then(function(response){
            
            setSound(current=>Uint8Array(response.data));
            setInfer(current=>true);
            
            console.log(response);
            console.log(response.data.length);
            const blob = new Blob([sound], { type: 'audio/wav' });
            console.log(blob);
            let url = (URL.createObjectURL(blob));
            // console.log(sound);
            setPlayer(<ReactAudioPlayer
                src={url}
                autoPlay
                controls
            />);

            console.log(url);
        }).catch(function (error){
            console.log(error);
        })
    }
    
    
    return (
    <>
        <section className="section">
			<h1 className="title">널위행</h1>
			<h2 className="subtitle">상품 알리미 서비스</h2>
		</section>
        
        <script className="jsbin" src="https://ajax.googleapis.com/ajax/libs/jquery/1/jquery.min.js"></script>
        <div>
            <div className="file-upload">
                <div className="image-upload-wrap">
                    <CameraFeed sendFile={imgHandler} />
                </div>
            </div>

            <br></br>
            {/* <h2>{infer === 0? "이미지를 입력하세요.":null}</h2>
            <h2>{infer ===-1? title[1] : null}</h2>
            <h2>{infer === 1? "예측 결과는 "+title[1] +"입니다.": null}</h2> */}
            
            {player}
            
        </div>
    </>
    );
}

export default Hang;
