import React, { useState } from "react";
import axios from "axios";

function Hang(){
    const [title,setTitle] = useState([]);
    const [myimage,setImage] = useState("");
    const [infer,setInfer] = useState(0);
    const [col,setCol] = useState("0");
    
    const fileHandler = (event)=>{
        setImage(event.target.files[0]);
    }
    const imgHandler = () =>{
        const formData = new FormData();
        formData.append('img',myimage);
        formData.append('col',col);
        axios.post("http://192.168.0.82:8000/app/imginf/",formData)
        .then(function(response){
            console.log(response);
            setTitle(current=>[response.data.a,response.data.b]);
            setInfer(current=>response.data.result);
        }).catch(function (error){
            console.log(error);
        })
        setImage("");
    }
    
    const btnHandler = (event)=>{
        setCol(event.target.value);
    }
    return (
    <>
        <section className="section">
			<h1 className="title">널위행</h1>
			<h2 className="subtitle">상품 알리미 서비스</h2>
		</section>
        
        <script className="jsbin" src="https://ajax.googleapis.com/ajax/libs/jquery/1/jquery.min.js"></script>
        <div>
            <div className="file-upload">
                <div className="image-upload-wrap">
                {!myimage&&(<input className="file-upload-input" type="file" name="profile" onChange={fileHandler} accept='image/jpg,image/png,image/jpeg'>
                        </input>)}
                    <div className="drag-text">
                        <h3>Drag and drop a file or select add Image</h3>
                    </div>
                </div>
                <div className="file-upload-content">
                    {myimage&&(<img className="file-upload-image" id="face-image" src={myimage} alt="your image">
                        </img>)}
                    <div className="image-title-wrap">
                        
                    </div>
                </div>
            </div>



            {/* <button value = {0} onClick={btnHandler}>ALL</button>
            <button value = {1} onClick={btnHandler}>가격</button>
            <button value = {2} onClick={btnHandler}>영양</button> */}
            <br></br>
            <button className="trigger" onClick={imgHandler}>이미지 전송</button>
            <h3>{col === "0"? "모든 정보":null}</h3>
            <h3>{col === "1"? "가격 정보":null}</h3>
            <h3>{col === "2"? "영양 정보":null}</h3>
            <h2>{infer === 0? "이미지를 입력하세요.":null}</h2>
            <h2>{infer ===-1? title[1] : null}</h2>
            <h2>{infer === 1? title[0]+" 예측 결과는 "+title[1] +"입니다.": null}</h2>

        </div>
    </>
    );
}

export default Hang;
