import React, { useState } from "react";
import axios from "axios";
import { CameraFeed } from './components/camera-feed';

function Hang(){
    const [title,setTitle] = useState([]);
    const [infer,setInfer] = useState(0);
    const imgHandler = async file =>{
        const formData = new FormData();
        formData.append('img',file);
        axios.post("http://192.168.0.82:8000/app/imginf/",formData)
        .then(function(response){
            console.log(response);
            setTitle(current=>[response.data.a,response.data.b]);
            setInfer(current=>response.data.result);
        }).catch(function (error){
            console.log(error);
        })
    }
    return (
    <>
        <section className="section">
			<h1 className="title">널위행</h1>
			<h2 className="subtitle">상품 알리미 서비스</h2>
		</section>
        
        <script className="jsbin" src="https://ajax.googleapis.com/ajax/libs/jquery/1/jquery.min.js"></script>
        <div>
            <div className="file-upload">
                <div className="image-upload-wrap">
                    <CameraFeed sendFile={imgHandler} />
                </div>
            </div>



            <br></br>
            <button className="trigger" onClick={imgHandler}>이미지 전송</button>
            <h2>{infer === 0? "이미지를 입력하세요.":null}</h2>
            <h2>{infer ===-1? title[1] : null}</h2>
            <h2>{infer === 1? " 예측 결과는 "+title[1] +"입니다.": null}</h2>

        </div>
    </>
    );
}

export default Hang;
