import React, { useState } from "react";
import axios from "axios";

function RestAPI(){
    const [title,setTitle] = useState("");
    const [myimage,setImage] = useState(null);
    const [infer,setInfer] = useState(false);
    const onChange = (event) =>{
        setTitle(event.target.value);
    }
    const onClick = () =>{
        const formData = new FormData();
        formData.append('title',title);
        formData.append('img',myimage);
        axios.post("http://localhost:8000/app/imglist/",formData)
        .then(function(response){
            console.log(response);
        }).catch(function (error){
            console.log(error);
        })
        setTitle("");
        setImage(null);
    }
    const imgHandler = () =>{
        const formData = new FormData();
        formData.append('img',myimage);
        axios.post("http://localhost:8000/app/imginf/",formData)
        .then(function(response){
            console.log(response);
            setTitle(current=>response.data.d);
            setInfer(current=>response.data.result);
        }).catch(function (error){
            console.log(error);
        })

    }
    
    const fileHandler = (event)=>{
        console.log(event);
        setImage(event.target.files[0]);
    }
    return (
    <>
        <h1>널 위 행</h1>
        <div>
            {/* <label htmlFor="title">제목</label>
            <input 
            id = "title"
            value={title}
            type="text"
            placeholder="제목"
            onChange={onChange}
            ></input> */}
            
            <br></br>

            {/* <label>이미지</label> */}
            <input 
                type="file"
                className="img"
                accept='image/jpg,image/png,image/jpeg'
                onChange={fileHandler}
            ></input>
            <h2>{infer ==-1? title : "이미지를 입력하세요."}</h2>
            <h2>{infer == 1? "예측 결과는 "+title +"입니다.": "이미지를 입력하세요."}</h2>
            {/* <button onClick={onClick}>POST</button> */}
            <button onClick={imgHandler}>이미지 전송</button>
        </div>
    </>
    );
}

export default RestAPI;
