import React, { useState } from "react";
import axios from "axios";

function RestAPI(){
    const [title,setTitle] = useState("");
    const [myimage,setImage] = useState(null);
    const [infer,setInfer] = useState(0);
    const [col,setCol] = useState(0);
    
    const imgHandler = () =>{
        const formData = new FormData();
        formData.append('img',myimage);
        axios.post("http://localhost:8000/app/imginf/",formData)
        .then(function(response){
            console.log(response);
            setTitle(current=>response.data.d);
            setInfer(current=>response.data.result);
        }).catch(function (error){
            console.log(error);
        })

    }
    
    const fileHandler = (event)=>{
        console.log(event);
        setImage(event.target.files[0]);
    }

    const btnHandler = (event)=>{
        console.log(event.target.value)
        setCol(event.target.value);
    }
    return (
    <>
        <h1>널 위 행</h1>
        <div>
            <input 
                type="file"
                className="img"
                accept='image/jpg,image/png,image/jpeg'
                onChange={fileHandler}
            ></input>
            <button value = {1} onClick={btnHandler}>가격</button>
            <button value = {2} onClick={btnHandler}>영양</button>

            <button onClick={imgHandler}>이미지 전송</button>
            <h3>{col === 0? "모든 정보":null}</h3>
            <h3>{col === 1? "가격 정보":null}</h3>
            <h3>{col === 2? "영양 정보":null}</h3>
            <h2>{infer === 0? "이미지를 입력하세요.":null}</h2>
            <h2>{infer ===-1? title : null}</h2>
            <h2>{infer === 1? "예측 결과는 "+title +"입니다.": null}</h2>

        </div>
    </>
    );
}

export default RestAPI;
